INSERT INTO screen VALUES (171, 1, '2006-01-09 00:00:00', 'Small Molecule', 'Act as antibacterial prodrugs.', NULL, NULL, 'A screen for compounds that act as antibacterial prodrugs.  This screen will use a pooled library of E. coli deletion mutants to identify compounds that are converted intracellularly into antimicrobial compounds.  A mutant lacking a converting enzyme is expected to survive treatment with a prodrug, whereas cells containing the converting enzyme will be killed.  This screen requires a counterscreen with wild-type E. coli.', '1/9/06: application will be submitted for review after satisfactory piloting data are acquired.', '', '', NULL, NULL, 241, 241, 547);
