INSERT INTO screen VALUES (270, 3, '2005-08-02 00:00:00', 'Small Molecule', 'Inhibit Ebola virus replication and/or transcription.', NULL, NULL, 'A screen for compounds that inhibit Ebola virus replication and/or transcription.', '', '', '', NULL, NULL, 396, 396, 515);
INSERT INTO screen VALUES (269, 3, '2005-10-19 00:00:00', 'Small Molecule', 'Inhibit vesicular stomatitis virus (VSV) glycoprotein-mediated entry into cells.', NULL, NULL, 'A screen for compounds that inhibit vesicular stomatitis virus (VSV) glycoprotein-mediated entry into cells.', 'This screen originated as a counterscreen within screen 477 (Ebola virus glycoprotein-mediated entry into cells).', '', '', NULL, NULL, 396, 396, 534);
INSERT INTO screen VALUES (271, 3, '2005-08-04 00:00:00', 'Small Molecule', 'Inhibit influenza virus replication and/or transcription.', NULL, NULL, 'A screen for compounds that inhibit influenza virus replication and/or transcription.', '', '', '', NULL, NULL, 396, 396, 516);
INSERT INTO screen VALUES (425, 0, '2004-07-06 00:00:00', 'Small Molecule', 'Stabilize the monomeric conformation of the VP40 matrix protein of filoviruses.', NULL, NULL, 'A screen for compounds that stabilize the monomeric conformation of the VP40 matrix protein of filoviruses.  This screen will employ a fluorescence polarization assay using fluorescein-labeled ssRNA.  Monomeric VP40 should not bind ssRNA under the assay conditions, and positive hits will be defined as compounds that block RNA binding.', 'This will likely be an Ebola screen, via NERCE.', NULL, NULL, NULL, NULL, 729, 729, 391);
INSERT INTO screen VALUES (22, 41, '2002-06-18 00:00:00', 'Small Molecule', 'Inhibit the  ATPase activity of  the mammalian chromosome-associated mitotic  kinesin KID.', NULL, NULL, 'A screen for compounds that inhibit the ATPase activity of  the mammalian chromosome-associated mitotic kinesin KID.   The ATPase activity of purified KID is assayed (in the absence of microtubules) using a malachite green assay and read in the plate reader.', '5/30/06 Tim says will not continue this screen. ', 'KID_ATPase', 'KID_ATPase', NULL, NULL, 671, 31, 227);
INSERT INTO screen VALUES (426, 0, '2002-01-30 00:00:00', 'Small Molecule', 'Are allosteric inhibitors/activators of 20S Proteasome activity--assay proteasome.', NULL, NULL, 'Screen for compounds that are allosteric inhibitors of 20S proteasome activity.  20S proteasomes, purified from muscle, are incubated in buffer with compound.  Proteasome activity is assayed by addition of the substrate Suc-LLVY-AMC, which fluoresces when cleaved by the proteasome proteases.', '5/9/03 Caroline talked to Alexei several months ago and he told her that there were one or two possible hits that were not followed up due to time constraints.', 'AKPROTEASOME', '20SACTIVITY', NULL, NULL, 456, 404, 134);
