INSERT INTO screen VALUES (327, 5, '2006-03-16 00:00:00', 'Small Molecule', 'Inhibit dengue virus cell fusion.', NULL, NULL, 'A screen for compounds that inhibit dengue virus cell fusion.', '2006-09: Jeff Cruzan has left the Harrison lab, and Aaron Schmidt is the new lead screener on this project.  The new screen number is 644.', '', '', NULL, NULL, 663, 165, 587);
INSERT INTO screen VALUES (429, 0, '2004-07-06 00:00:00', 'Small Molecule', 'Inhibit eukaryotic protein synthesis.', NULL, NULL, 'A screen for compounds that inhibit eukaryotic protein synthesis.  This screen has been piloted in 96-well format and is being converted to 384-well format.  The assay involves a dual luciferase system to identify protein synthesis inhibitors of various different classes.', '', '', '', NULL, NULL, 36, 36, 395);
INSERT INTO screen VALUES (343, 3, '2006-06-09 00:00:00', 'RNAi', 'An siRNA screen to identify biochemical pathways that intersect with dengue virus replication within host cells. ', NULL, NULL, 'An siRNA screen to identify biochemical pathways that intersect with dengue virus replication within host cells. The assay will be read out by screening microscope.', '6/5/06: "We plan initially to screen specific human siRNA subsets that target kinases, druggable genome and GPC receptors in order to identify known biochemical pathways that intersect with dengue virus replication within host cells." ', '', '', NULL, NULL, 502, 268, 624);
INSERT INTO screen VALUES (344, 3, '2006-06-02 00:00:00', 'Small Molecule', 'Inhibit dengue virus replication.', NULL, NULL, 'A screen for compounds that inhibit dengue virus replication..', '8/31/06: Piloting experiments will be conducted with both immunostaining- and FRET-based protocols.  The FRET assay is simpler and thus preferred, but it is not known at this time whether it will work.  The imaging screen has been piloted to some degree but is perhaps too labor-intensive for a large-scale screen.
INSERT INTO screen VALUES (342, 3, '2006-08-31 00:00:00', 'Small Molecule', 'Modulate poliovirus replication.', NULL, '2006-12-05 00:00:00', 'A screen for compounds that modulate poliovirus replication.', '2006-10-06: This screen consist of screening only known bioactives as a piloting study for a dengue FRET-based assay (screen 623).', '', '', NULL, NULL, 268, 268, 642);
INSERT INTO screen VALUES (345, 3, '2005-01-11 00:00:00', 'Small Molecule', 'Inhibit dengue virus replication.', NULL, NULL, 'A screen for compounds that inhibit dengue virus replication.', '2006-06-01: this screen is formally closed and will be reopened under screen ID 623 with Justin Chu as the new lead screener.', '', '', NULL, NULL, 697, 268, 467);
INSERT INTO screen VALUES (430, 0, '2006-02-07 00:00:00', 'Small Molecule', 'Inhibit the PknA kinase of M. tuberculosis.', NULL, NULL, 'A screen for compounds that inhibit the PknA kinase of M. tuberculosis.', '', '', '', NULL, NULL, 121, 121, 564);
