INSERT INTO screen VALUES (28, 41, '2003-03-19 00:00:00', 'Small Molecule', 'Inhibit the ATPase activity of  the human mitotic kinesin MKLP1 in the presence of microtubules.', NULL, NULL, 'A screen for compounds that inhibit the ATPase activity of  the human mitotic kinesin MKLP1 in the presence of microtubules.   The ATPase activity of purified MKLP1 is assayed in the presence of microtubules using a malachite green assay and read in the plate reader.', 'MKLP1 from Glotzer lab, IMP Vienna.', 'MKLP', 'MKLP', NULL, NULL, 559, 31, 273);
INSERT INTO screen VALUES (29, 41, '2004-07-26 00:00:00', 'Small Molecule', 'Inhibit the ATPase activity of  the motor domain of the Xenopus mitotic kinesin KIF2 in the presence of microtubules.', NULL, NULL, 'A screen for compounds that inhibit the ATPase activity of  the motor domain of the Xenopus mitotic kinesin KIF2 in the presence of microtubules.  The ATPase activity of purified KIF2 is assayed in the presence of microtubules using a malachite green assay and read in the plate reader.', 'Biolgical follow-up of hits will be carried out by Ryoma (Puck) Ohi.  Protein is very similar to XKCM1, screened by Shala Movahedi (#377).  ', NULL, NULL, NULL, NULL, 559, 31, 409);
INSERT INTO screen VALUES (30, 41, '2004-06-16 00:00:00', 'Small Molecule', 'Inhibit the microtubule-dependent ATPase activity of Eg5-367H(P121A), a (P121A) point mutant of the N-terminal motor domain of human Eg5. ', NULL, NULL, 'A screen for compounds that inhibit the microtubule-dependent ATPase activity of Eg5-367H(P121A), a (P121A) point mutant of the N-terminal motor domain of human Eg5. The assay uses malachite green to measure the phosphate generated upon microtubule-stimulated ATP hydrolysis by Eg5.', NULL, NULL, NULL, NULL, NULL, 559, 31, 374);
