INSERT INTO screen VALUES (110, 33, '2004-01-08 00:00:00', 'Small Molecule', 'Inhibit the activity of the yeast histone deacetylase (HDAC) Rpd3.', NULL, NULL, 'A screen for compounds that inhibit the activity of the yeast histone deacetylase (HDAC) Rpd3.', NULL, NULL, NULL, NULL, NULL, 557, 35, 335);
INSERT INTO screen VALUES (119, 33, '2002-02-28 00:00:00', 'Small Molecule', 'Overcome the inhibition of cell proliferation caused by the tyrosine kinase inhibitor DAPH. ', NULL, NULL, 'A screen for compounds that overcome the inhibition of cell proliferation caused by the tyrosine kinase inhibitor DAPH. A549 (human lung carcinoma) cells are grown the presence of DAPH and compound for 20 hours.  The ability of the cells to proliferate is then assayed by measuring BrdU incorporation over 3 hours (cells are stained with Abs against BrdU).', NULL, 'null', NULL, NULL, NULL, 282, 35, 174);
INSERT INTO screen VALUES (120, 33, '2002-12-11 00:00:00', 'Small Molecule', 'Suppress the growth-inhibiting effects of rapamycin in the yeast S. cerevisiae (wild-type).', NULL, NULL, 'A screen for compounds that suppress the growth-inhibiting effects of rapamycin in the yeast S. cerevisiae (wild-type).  Compound is added to cells that have been cultured in rapamycin and growth is scored by eye two days later.', NULL, NULL, NULL, NULL, NULL, 681, 35, 252);
INSERT INTO screen VALUES (170, 7, '2004-07-06 00:00:00', 'Small Molecule', 'Increase the number of hematopoetic stem cells in zebrafish embryos.', '2004-07-13 00:00:00', '2004-07-13 00:00:00', 'A screen for compounds that increase the number of hematopoetic stem cells in zebrafish embryos.', '05/19/2006 - Talked with Jill deJong by phone. Out of 2000 compounds screened, 50 appeared to cause the desired phenotype (the mutant embryos had increased numbers of hematopoetic stem cells, by staining).  However, none of these compounds (known bioactives reordered from commercial sources) retested in her secondary assays. Jill therefore has developed a new assay, involving whole mount in situ staining, which will be entered with a new screen number. From this screen (#389) however, Jill did get usable data with regard to library compounds that are toxic or teratogenic to zebrafish, or that cause delays in embryonic development. Jill will provide annotation of those compounds for Screensaver (associated with screen #389). ', '', '', NULL, NULL, 473, 133, 389);
