INSERT INTO screen VALUES (111, 33, '2002-07-03 00:00:00', 'Small Molecule', 'Suppress dephosphorylation of S6 protein after inhibition of insulin signalling.', NULL, NULL, 'A screen for compounds that suppress dephosphorylation of S6 protein after inhibition of insulin signalling.', '', '', '', NULL, NULL, 568, 35, 241);
INSERT INTO screen VALUES (112, 33, '2002-07-03 00:00:00', 'Small Molecule', 'Suppress dephosphorylation of S6 protein in the presence of rapamycin.', NULL, NULL, 'A screen for compounds that suppress dephosphorylation of S6 protein in the presence of rapamycin.  This assay is carried out using A549 (human lung carcinoma) cells.  The cells are treated with rapamycin. In the absence of additional treatment, this prevents phosphorylation of S6 via the FRAP pathway.  Compounds are then added to the cells and S6 phosphorylation is monitored by cytoblot assay.  Hits are compounds that result in S6 phosphoylation.  ', '', '', '', NULL, NULL, 568, 35, 238);
INSERT INTO screen VALUES (113, 33, '2002-06-07 00:00:00', 'Small Molecule', 'Suppress dephosphorylation of S6 protein in response to amino acid starvation.', NULL, NULL, 'A screen for compounds that suppress dephosphorylation of S6 protein in response to amino acid starvation. This assay is carried out using A549 (human lung carcinoma) cells.  The cells are starved for amino acids. In the absence of additional treatment, this prevents phosphorylation of S6 via the FRAP pathway.  Compounds are added to starved cells and S6 phosphorylation is monitored by cytoblot assay.  Hits are compounds that result in S6 phosphoylation.  ', NULL, 'S6', 'S6AminoAcidStarvation', NULL, NULL, 568, 35, 209);
