INSERT INTO screen VALUES (121, 33, '2003-10-03 00:00:00', 'Small Molecule', 'Differentially affect the growth of  the diploid S. cerevisiae yeast strain S288C at 30 C and 41 C.', NULL, NULL, 'A screen for compounds that differentially affect the growth of  the diploid S. cerevisiae yeast strain S288C at 30 C and 41 C.  Compound is added to cells growing in rich and growth is scored 24-48 hours later.', 'Note: no drug sensitizing mutations will be crossed into the S288C "wt" lab strain.  INSERT INTO screen VALUES (122, 33, '2003-10-03 00:00:00', 'Small Molecule', 'Differentially affect the growth of  the diploid S. cerevisiae yeast strain YJM145/S288C ("clinical strain"/"lab strain" heterozygote) at 30 C and 41 C.', NULL, NULL, 'A screen for compounds that differentially affect the growth of  the S. cerevisiae yeast strain YJM145/S288C ("clinical strain"/"lab strain" heterozygote)  at 30 C and 41 C.  Compound is added to cells growing in rich and growth is scored 24-48 hours later.', 'Note: no drug sensitizing mutations will be crossed into YJM145, however it is believed  (by Steinmetz et al.) that the strain is drug senstitive.  INSERT INTO screen VALUES (123, 33, '2004-06-11 00:00:00', 'Small Molecule', 'Activate expression from the OCT4 promoter in a mouse  fibroblast cell line.', NULL, NULL, 'A screen for compounds that activate expression from the OCT4 promoter in a mouse  fibroblast cell line.', 'Kristen Brennand of the Melton lab is a collaborator on this screen.
INSERT INTO screen VALUES (124, 33, '2003-10-03 00:00:00', 'Small Molecule', 'Differentially affect the growth of  the diploid S. cerevisiae yeast strain YJM145 (a "clinical isolate") at 30 C and 41 C.', NULL, NULL, 'A screen for compounds that differentially affect the growth of  the diploid S. cerevisiae yeast strain YJM145 (a "clinical isolate")  at 30 C and 41 C.  Compound is added to cells growing in rich and growth is scored 24-48 hours later.', 'Note: no drug sensitizing mutations will be crossed into YJM145, however it is believed  (by Steinmetz et al.) that the strain is drug senstitive.  INSERT INTO screen VALUES (125, 33, '2004-09-02 00:00:00', 'Small Molecule', 'Supress or enhance the effects of wortmannin on the growth of the yeast S. cerevisiae.', NULL, NULL, 'A screen for compounds that supress or enhance the effects of wortmannin on the growth of the yeast S. cerevisiae.', NULL, NULL, NULL, NULL, NULL, 111, 35, 416);
