INSERT INTO screen VALUES (99, 33, '2002-05-14 00:00:00', 'Small Molecule', 'Affect mitochondrial function in Sf-1 cells (primary human fibroblasts).', NULL, NULL, 'A screen for compounds that affect mitochondrial function in Sf-1 cells (primary human fibroblasts).  After compound addition, mitochondrial potential is assayed using a JC-1 based fluorimetric assay.', 'Carrying out the same screen as Bimal Desai, but screening Dave Spring''s biaryl library--Copy A.  He wants to transfer 200-300 nL of compound to each well.', NULL, NULL, NULL, NULL, 371, 35, 194);
