INSERT INTO screen VALUES (132, 33, '2005-02-16 00:00:00', 'Small Molecule', 'suppress S6 phosphorylation in A549 cells under normal growth conditions', '2005-02-24 00:00:00', '2005-02-24 00:00:00', 'A screen for compounds that suppress S6 phosphorylation in A549 cells under normal growth conditions.  A549 cells will be cultured, small molecules added and S6 phosphorylation detected using a primary antibody to phospho-S6 and an HRP-coupled secondary antibody.  Luminescence will be detected on a plate reader.', 'NT 02/16/05: Jiong is taking over this screen from Ali Shamji (previously listed as screen ID: 240)', NULL, NULL, NULL, NULL, 288, 35, 471);
INSERT INTO screen VALUES (431, 0, '2006-09-15 00:00:00', 'Small Molecule', 'Inhibit the CapD enzyme of Bacillus anthracis.', NULL, '2006-10-23 00:00:00', 'A screen for compounds that inhibit the CapD enzyme of Bacillus anthracis.  CapD is involved in synthesis and surface localization of the poly-gamma-D-glutamate (PDGA) capsule, one of the principal virulence factors during anthrax infection.  CapD mutant strains of B. anthracis lack covalently attached PDGA and are characterized by a significant loss of virulence in animal models.', '', '', '', NULL, NULL, 700, 15, 645);
